function setupPasswordToggle(s,o){const a=$("#"+s),n=$("#"+o);n.on("click",function(){"password"===a.attr("type")?(a.attr("type","text"),n.removeClass("icon-yincangbukejian").addClass("icon-xianshikejian")):(a.attr("type","password"),n.removeClass("icon-xianshikejian").addClass("icon-yincangbukejian"))})}window.onload=function(){setupPasswordToggle("passwordInput","showPassword"),setupPasswordToggle("passwordInput1","showPassword1"),setupPasswordToggle("passwordInput2","showPassword2")};